SELECT beautician_id AS beauticianId, IFNULL( SUM( product_price ) , 0 ) AS amount, IFNULL( COUNT( product_name ) , 0 ) AS oderNumber FROM beautician_trade_detail WHERE store_id = #{storeId} AND trade_type = 1 AND trade_status = 1
and beautician_type = #{selectType,jdbcType=BIT}
AND settlement_time >= #{startDate}
AND settlement_time <= #{endDate}
GROUP BY beautician_id ORDER BY amount DESC

